create table sentinel.support_tickets (
    id               number
        generated by default as identity
    not null enable,
    status_rc        varchar2(20 byte) not null enable,
    priority         varchar2(10 byte) not null enable,
    subject          varchar2(200 byte) not null enable,
    description      clob,
    assigned_to      number,
    resolved_at      timestamp(6),
    category         varchar2(50 byte),
    department       varchar2(50 byte),
    customer_email   varchar2(100 byte),
    customer_name    varchar2(100 byte),
    resolution_notes clob,
    created_by       number not null enable,
    created_at       timestamp(6) default current_timestamp not null enable,
    updated_at       timestamp(6) default current_timestamp not null enable,
    updated_by       number
);

alter table sentinel.support_tickets add primary key ( id )
    using index enable;


